import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Pie } from 'react-chartjs-2';

function CreadorEncuesta() {
    const { id } = useParams();
    const [encuesta, setEncuesta] = useState(null);

    useEffect(() => {
        const fetchEncuesta = async () => {
            try {
                const response = await fetch(`http://188.127.169.12:8000/encuesta/${id}`);
                if (!response.ok) {
                    throw new Error('No se pudo obtener la encuesta');
                }
                const data = await response.json();
                setEncuesta(data);
            } catch (error) {
                console.error(error);
            }
        };

        fetchEncuesta();
    }, [id]);

    if (!encuesta) {
        return <p></p>;
    }

    const { titulo, descripcion, fecha, dueno, opciones } = encuesta;

    // Preparar datos para el gráfico de pastel
    const labels = opciones.map(opcion => opcion.texto);
    const data = opciones.map(opcion => opcion.votos);
    const backgroundColors = ['#FF6384', '#36A2EB', '#FFCE56']; // Colores para cada sección del gráfico

    const pieData = {
        labels: labels,
        datasets: [{
            data: data,
            backgroundColor: backgroundColors
        }]
    };

    return (
        <section className="bg-white">
            <div className="mx-auto max-w-screen-xl px-4 py-12 sm:px-6 md:py-16 lg:px-8">
                <div className="flex flex-col rounded-lg bg-blue-50 px-4 py-8 text-center">
                    <h2 className="text-3xl font-bold text-blue-600 sm:text-4xl">{titulo}</h2>
                </div>

                <div className="mt-8 sm:mt-12">
                    <dl className="grid grid-cols gap-4 sm:grid-cols-3">
                        {opciones.map((opcion, index) => (
                            <div key={index} className="flex flex-col rounded-lg bg-green-50 px-4 py-8 text-center">
                                <dd className="text-4xl font-extrabold text-green-600 md:text-5xl">{opcion.texto}</dd>
                            </div>
                        ))}
                        <div className="flex flex-col rounded-lg bg-white-50 px-4 py-8 text-center">
                            <dt className="order-last text-lg font-medium text-gray-500">ID</dt>
                            <dd className="text-4xl font-extrabold text-blue-600 md:text-5xl">{id}</dd>
                        </div>

                        <div className="flex flex-col rounded-lg bg-white-50 px-4 py-8 text-center">
                            <dt className="order-last text-lg font-medium text-gray-500">Tiempo</dt>
                            <dd className="text-4xl font-extrabold text-blue-600 md:text-5xl">{fecha}</dd>
                        </div>

                        <div className="flex flex-col rounded-lg bg-white-50 px-4 py-8 text-center">
                            <dt className="order-last text-lg font-medium text-gray-500">Usuario</dt>
                            <dd className="text-4xl font-extrabold text-blue-600 md:text-5xl">{dueno}</dd>
                        </div>


                    </dl>
                </div>

                {/* Gráfico de pastel */}
                <div className="mt-8">
                    <Pie data={pieData} />
                </div>
            </div>

        </section>
    );
}

export default CreadorEncuesta;
