import React, { useState } from 'react';

function EncuestaFormulario() {
    // Definir estado para el título y el dueño
    const [titulo, setTitulo] = useState('');
    const [dueno, setDueno] = useState('');

    // Función para manejar el envío del formulario
    const handleSubmit = async (event) => {
        event.preventDefault();  // Prevenir el comportamiento predeterminado del formulario

        const url = `http://localhost:8000/encuestas/`; // URL de la API

        try {
            // Enviar la solicitud POST a la API
            const response = await fetch(url, {
                method: 'POST',  // Método HTTP
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ titulo, dueno: parseInt(dueno, 10) })  // Cuerpo de la solicitud (convertir dueno a entero)
            });

            if (!response.ok) {
                throw new Error(`Error: ${response.status}`);
            }

            const result = await response.json(); // Suponiendo que la API responde con JSON
            console.log('Encuesta creada:', result);
            alert('Encuesta creada con éxito!');
        } catch (error) {
            console.error('Error al crear la encuesta:', error);
            alert('Error al crear la encuesta');
        }

        setTitulo('');
        setDueno('');
    };

    return (
        <form onSubmit={handleSubmit}>
            <label
                for="tituloEncuesta"
                class="relative block rounded-md border border-gray-200 shadow-sm focus-within:border-blue-600 focus-within:ring-1 focus-within:ring-blue-600"
            >
                <input
                    type="text"
                    id="tituloEncuesta"
                    class="peer border-none bg-transparent placeholder-transparent focus:border-transparent focus:outline-none focus:ring-0"
                    placeholder="Título de la encuesta"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <span
                    class="pointer-events-none absolute start-2.5 top-0 -translate-y-1/2 bg-white p-0.5 text-xs text-gray-700 transition-all peer-placeholder-shown:top-1/2 peer-placeholder-shown:text-sm peer-focus:top-0 peer-focus:text-xs"
                >
                    Título de la encuesta
                </span>
            </label>
            <label
                for="duenoEncuesta"
                class="relative block rounded-md border border-gray-200 shadow-sm focus-within:border-blue-600 focus-within:ring-1 focus-within:ring-blue-600"
            >
                <input
                    type="text"
                    id="duenoEncuesta"
                    class="peer border-none bg-transparent placeholder-transparent focus:border-transparent focus:outline-none focus:ring-0"
                    placeholder="ID del dueño de la encuesta"
                    value={dueno}
                    onChange={(e) => setDueno(e.target.value)}
                    required
                />
                <span
                    class="pointer-events-none absolute start-2.5 top-0 -translate-y-1/2 bg-white p-0.5 text-xs text-gray-700 transition-all peer-placeholder-shown:top-1/2 peer-placeholder-shown:text-sm peer-focus:top-0 peer-focus:text-xs"
                >
                    ID del dueño
                </span>
            </label>
            <button type="submit" class="mt-4">Crear Encuesta</button>
        </form>
    );
}

export default EncuestaFormulario;
