import React, { useState } from 'react';
import NotaFormulario from '../components/NotaFormulario';
import { Button } from '@nextui-org/react';
import EncuestaFormulario from './EncuestaFormulario'; // Ajusta la ruta si es necesario

function CreadorEncuesta() {
    const [mostrarFormulario, setMostrarFormulario] = useState(false);

    const toggleFormulario = () => {
        setMostrarFormulario(!mostrarFormulario);
    };

    return (
        <div>
            <Button onClick={toggleFormulario}>
                {mostrarFormulario ? 'Cerrar Formulario' : 'Crear Encuesta'}
            </Button>

            {mostrarFormulario && (
                <EncuestaFormulario control={toggleFormulario} />
            )}

            <NotaFormulario />
        </div>
    );
}

export default CreadorEncuesta;
